package strtoother;

public class conversionofstrtodiff {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		
        String str = "123";
	    int a = Integer.parseInt(str);
          System.out.println("str to int:"+a);
          float b =Float.parseFloat(str);
          System.out.println("str to float" +b);
          double c =Double.parseDouble(str);
          System.out.println("str to double " +c);
          byte d = Byte.parseByte(str);
          System.out.println("str to byte " +d);
          short e =Short.parseShort(str);
          System.out.println("str to short " +e);
          long f = Long.parseLong(str);
          System.out.println("str to long " +f);
  
	}

}
